    position: absolute;
    text-align: center;
    font-size: 25px;
    font-family: IcoMoon;
    height: 63px;
    margin: 21px 0 0 0;
    padding:0;
    width: 63px;
}

.rev_slider_wrapper .tp-leftarrow:before{
	margin-left: -2px;
}

.rev_slider_wrapper .tp-rightarrow:before{
	margin-left: -1px;
}

.rev_slider_wrapper .tp-rightarrow{
	left:auto;
	right:0;
}

